irro\" pattern='^[ 0-9a-zA-Z\\b]+$' required>\n                    </div>\n                    \n                    <div class=\"uk-width-1-6@s\">\n                        <input class=\"uk-input\" type=\"text\" data-mask=\"(00) 0000-0000\" placeholder=\"Telefone Fixo\" name=\"cd_telefone_responsavel\" pattern=\".{12,}\">\n                    </div>\n                    \n                    <div class=\"uk-width-1-6@s\">\n                        <input class=\"uk-input\" type=\"text\" data-mask=\"(00) 00000-0000\" placeholder=\"Celular\" name=\"cd_celular_responsavel\" pattern=\".{13,}\" required>\n                    </div>\n                    \n                    <div class=\"uk-width-1-2@s uk-margin-large uk-align-center \">\n                        <button class=\"uk-button uk-button-primary uk-width-1-1 background-blue-light\">CADASTRAR</button>\n                    </div>\n                </form>\n            </div>\n        </div>\n    </body>\n@endsection","undoManager":{"mark":73,"position":100,"stack":[[{"start":{"row":14,"column":16},"end":{"row":14,"column":22},"action":"remove","lines":["<?php "],"id":28},{"start":{"row":14,"column":16},"end":{"row":14,"column":17},"action":"insert","lines":["@"]}],[{"start":{"row":14,"column":22},"end":{"row":14,"column":23},"action":"remove","lines":[";"],"id":29}],[{"start":{"row":14,"column":22},"end":{"row":14,"column":23},"action":"remove","lines":[" "],"id":30}],[{"start":{"row":14,"column":22},"end":{"row":14,"column":23},"action":"remove","lines":["?"],"id":31}],[{"start":{"row":14,"column":22},"end":{"row":14,"column":23},"action":"remove","lines":[">"],"id":32}],[{"start":{"row":9,"column":43},"end":{"row":9,"column":44},"action":"remove","lines":[" "],"id":33}],[{"start":{"row":9,"column":43},"end":{"row":9,"column":44},"action":"remove","lines":[":"],"id":34}],[{"start":{"row":9,"column":43},"end":{"row":9,"column":44},"action":"remove","lines":[" "],"id":35}],[{"start":{"row":9,"column":43},"end":{"row